(1, 'BAL_ADD', 'Balance - Added', 'Your Account has been Credited', '<div><div style=\"font-family: Montserrat, sans-serif;\">{{amount}} {{site_currency}} has been added to your account .</div><div style=\"font-family: Montserrat, sans-serif;\"><br></div><div style=\"font-family: Montserrat, sans-serif;\">Transaction Number : {{trx}}</div><div style=\"font-family: Montserrat, sans-serif;\"><br></div><span style=\"color: rgb(33, 37, 41); font-family: Montserrat, sans-serif;\">Your Current Balance is :&nbsp;</span><font style=\"font-family: Montserrat, sans-serif;\"><span style=\"font-weight: bolder;\">{{post_balance}}&nbsp; {{site_currency}}&nbsp;</span></font><br></div><div><font style=\"font-family: Montserrat, sans-serif;\"><span style=\"font-weight: bolder;\"><br></span></font></div><div>Admin note:&nbsp;<span style=\"color: rgb(33, 37, 41); font-size: 12px; font-weight: 600; white-space: nowrap; text-align: var(--bs-body-text-align);\">{{remark}}</span></div>', '{{amount}} {{site_currency}} credited in your account. Your Current Balance {{post_balance}} {{site_currency}} . Transaction: #{{trx}}. Admin note is \"{{remark}}\"', '{\"trx\":\"Transaction number for the action\",\"amount\":\"Amount inserted by the admin\",\"remark\":\"Remark inserted by the admin\",\"post_balance\":\"Balance of the user after this transaction\"}', 1, 1, '2021-11-03 12:00:00', '2024-04-20 05:57:19'),
(2, 'BAL_SUB', 'Balance - Subtracted', 'Your Account has been Debited', '<div style=\"font-family: Montserrat, sans-serif;\">{{amount}} {{site_currency}} has been subtracted from your account .</div><div style=\"font-family: Montserrat, sans-serif;\"><br></div><div style=\"font-family: Montserrat, sans-serif;\">Transaction Number : {{trx}}</div><div style=\"font-family: Montserrat, sans-serif;\"><br></div><span style=\"color: rgb(33, 37, 41); font-family: Montserrat, sans-serif;\">Your Current Balance is :&nbsp;</span><font style=\"font-family: Montserrat, sans-serif;\"><span style=\"font-weight: bolder;\">{{post_balance}}&nbsp; {{site_currency}}</span></font><br><div><font style=\"font-family: Montserrat, sans-serif;\"><span style=\"font-weight: bolder;\"><br></span></font></div><div>Admin Note: {{remark}}</div>', '{{amount}} {{site_currency}} debited from your account. Your Current Balance {{post_balance}} {{site_currency}} . Transaction: #{{trx}}. Admin Note is {{remark}}', '{\"trx\":\"Transaction number for the action\",\"amount\":\"Amount inserted by the admin\",\"remark\":\"Remark inserted by the admin\",\"post_balance\":\"Balance of the user after this transaction\"}', 1, 1, '2021-11-03 12:00:00', '2022-04-03 02:24:11'),
(3, 'DEPOSIT_COMPLETE', 'Deposit - Automated - Successful', 'Deposit Completed Successfully', '<div><p>Hi there,</p><p>We are pleased to inform you that your deposit of {{amount}} {{site_currency}} via {{method_name}} has been completed successfully. Here are the details of your transaction:</p><p><strong>Deposit Details</strong>:</p><ul><li><strong>Amount</strong>: {{amount}} {{site_currency}}</li><li><strong>Charge</strong>: {{charge}} {{site_currency}}</li><li><strong>Conversion Rate</strong>: 1 {{site_currency}} = {{rate}} {{method_currency}}</li><li><strong>Received</strong>: {{method_amount}} {{method_currency}}</li><li><strong>Paid via</strong>: {{method_name}}</li><li><strong>Transaction Number</strong>: {{trx}}</li></ul><p>If you have any questions or need further assistance, feel free to contact us.</p><p>Thanks,</p></div>', 'We are pleased to inform you that your deposit of {{amount}} {{site_currency}} via {{method_name}} has been completed successfully.', '{\"trx\":\"Transaction number for the deposit\",\"amount\":\"Amount inserted by the user\",\"charge\":\"Gateway charge set by the admin\",\"rate\":\"Conversion rate between base currency and method currency\",\"method_name\":\"Name of the deposit method\",\"method_currency\":\"Currency of the deposit method\",\"method_amount\":\"Amount after conversion between base currency and method currency\",\"post_balance\":\"Balance of the user after this transaction\"}', 1, 1, '2021-11-03 12:00:00', '2024-06-08 09:03:03'),
(4, 'DEPOSIT_APPROVE', 'Deposit - Manual - Approved', 'Your Deposit is Approved', '<div style=\"font-family: Montserrat, sans-serif;\"><p>Hi there,</p><p>We are pleased to inform you that your deposit request of {{amount}} {{site_currency}} via {{method_name}} has been approved.</p><p><strong>Details of your Deposit</strong>:</p><ul><li><strong>Amount</strong>: {{amount}} {{site_currency}}</li><li><strong>Charge</strong>: {{charge}} {{site_currency}}</li><li><strong>Conversion Rate</strong>: 1 {{site_currency}} = {{rate}} {{method_currency}}</li><li><strong>Received</strong>: {{method_amount}} {{method_currency}}</li><li><strong>Paid via</strong>: {{method_name}}</li><li><strong>Transaction Number</strong>: {{trx}}</li></ul><p>Your current balance is now {{post_balance}} {{site_currency}}.</p><p>If you have any questions or need further assistance, feel free to contact us.</p><p>Thanks,</p></div>', 'We are pleased to inform you that your deposit request of {{amount}} {{site_currency}} via {{method_name}} has been approved.', '{\"trx\":\"Transaction number for the deposit\",\"amount\":\"Amount inserted by the user\",\"charge\":\"Gateway charge set by the admin\",\"rate\":\"Conversion rate between base currency and method currency\",\"method_name\":\"Name of the deposit method\",\"method_currency\":\"Currency of the deposit method\",\"method_amount\":\"Amount after conversion between base currency and method currency\",\"post_balance\":\"Balance of the user after this transaction\"}', 1, 1, '2021-11-03 12:00:00', '2024-06-08 09:05:04'),
(5, 'DEPOSIT_REJECT', 'Deposit - Manual - Rejected', 'Your Deposit Request is Rejected', '<div style=\"font-family: Montserrat, sans-serif;\"><p>Hi there,</p><p>We regret to inform you that your deposit request of {{amount}} {{site_currency}} via {{method_name}} has been rejected.</p><p><br></p><p><strong>Details of the Transaction</strong>:</p><p><br></p><ul><li><strong>Conversion Rate</strong>: 1 {{site_currency}} = {{rate}} {{method_currency}}</li><li><strong>Received</strong>: {{method_amount}} {{method_currency}}</li><li><strong>Paid via</strong>: {{method_name}}</li><li><strong>Charge</strong>: {{charge}}</li><li><strong>Transaction Number</strong>: {{trx}}</li><li><br></li></ul><p>{{rejection_message}}</p><p><br></p><p>If you have any queries, feel free to contact us.</p><p>Thanks,</p></div>', 'We regret to inform you that your deposit request of {{amount}} {{site_currency}} via {{method_name}} has been rejected.\r\n\r\n{{rejection_message}}', '{\"trx\":\"Transaction number for the deposit\",\"amount\":\"Amount inserted by the user\",\"charge\":\"Gateway charge set by the admin\",\"rate\":\"Conversion rate between base currency and method currency\",\"method_name\":\"Name of the deposit method\",\"method_currency\":\"Currency of the deposit method\",\"method_amount\":\"Amount after conversion between base currency and method currency\",\"rejection_message\":\"Rejection message by the admin\"}', 1, 1, '2021-11-03 12:00:00', '2024-06-08 09:07:07'),
(6, 'DEPOSIT_REQUEST', 'Deposit - Manual - Requested', 'Deposit Request Submitted Successfully', '<div><p>Hi there,</p><p>We are pleased to inform you that your deposit request of {{amount}} {{site_currency}} via {{method_name}} has been submitted successfully.</p><p><br></p><p><strong>Details of your Deposit</strong>:</p><p><br></p><ul><li><strong>Amount</strong>: {{amount}} {{site_currency}}</li><li><strong>Charge</strong>: {{charge}} {{site_currency}}</li><li><strong>Conversion Rate</strong>: 1 {{site_currency}} = {{rate}} {{method_currency}}</li><li><strong>Payable</strong>: {{method_amount}} {{method_currency}}</li><li><strong>Pay via</strong>: {{method_name}}</li><li><br></li><li><strong>Transaction Number</strong>: {{trx}}</li><li><br></li></ul><p>If you have any questions or need further assistance, feel free to contact us.</p><p>Thanks,</p></div>', 'We are pleased to inform you that your deposit request of {{amount}} {{site_currency}} via {{method_name}} has been submitted successfully.', '{\"trx\":\"Transaction number for the deposit\",\"amount\":\"Amount inserted by the user\",\"charge\":\"Gateway charge set by the admin\",\"rate\":\"Conversion rate between base currency and method currency\",\"method_name\":\"Name of the deposit method\",\"method_currency\":\"Currency of the deposit method\",\"method_amount\":\"Amount after conversion between base currency and method currency\"}', 1, 1, '2021-11-03 12:00:00', '2024-06-08 09:08:46'),
